import React, { useState } from "react";
import { useRouteMatch, NavLink, Link } from "react-router-dom";
import { FaTwitter, FaGithub, FaInstagram, FaLinkedin } from "react-icons/fa";
// Components
// import DemoOptions from "../../components/common/DemoOptions";

const links = [
    {
        title: "Home",
        path: "/intro",
    },
    {
        title: "About",
        path: "/about",
    },
    // {
    //     title: "Resume",
    //     path: "/resume",
    // },
    // {
    //     title: "Service",
    //     path: "/service",
    // },
    // {
    //     title: "Portfolio",
    //     path: "/portfolio",
    // },
    {
        title: "Portfolio",
        path: "/blog",
    },
    // {
    //     title: "Testimonial",
    //     path: "/testimonial",
    // },
    {
        title: "Contact",
        path: "/contact",
    },
];

function Header() {
    let { url } = useRouteMatch();

    const [displaySidenav, setDisplaySidenav] = useState(false);

    const handleClick = (e) => {
        e.preventDefault();
        setDisplaySidenav(!displaySidenav);
    };

    return (
        <>
            <header className="app-header app-header-1">
                <div className="header-logo">
                    <Link to={`url`.replace(/([^:])(\/\/+)/g, "$1/")}>Ally Deeter</Link>
                </div>
                <button className="button button-menu" onClick={(e) => handleClick(e)}>
                    <span></span>
                </button>
                <ul className="header-dots">
                    {links.map((item, index) => (
                        <li key={index}>
                            <NavLink to={`${url}${item.path}`} activeClassName="active">
                                <span className="circle"></span>
                            </NavLink>
                        </li>
                    ))}
                </ul>
                <ul className="header-menu">
                    {links.map((item, index) => (
                        <li key={index}>
                            <NavLink to={`${url}${item.path}`} activeClassName="active">
                                <span className="anchor">{item.title}</span>
                            </NavLink>
                        </li>
                    ))}
                </ul>
                <div className="header-follow">
                    <ul>
                        <li>
                            <a href="https://github.com/Ardeeter" target="_blank" rel="noopener noreferrer">
                                <FaGithub className="icon" />
                            </a>
                        </li>
                        <li>
                            <a href="http://www.linkedin.com/in/ally-deeter" target="_blank" rel="noopener noreferrer">
                                <FaLinkedin className="icon" />
                            </a>
                        </li>
                        {/* <li>
                            <a href="/#">
                                <i className="ti-twitter-alt"></i>
                            </a>
                        </li>
                        <li>
                            <a href="/#">
                                <i className="ti-github"></i>
                            </a>
                        </li>
                        <li>
                            <a href="/#">
                                <i className="ti-vimeo-alt"></i>
                            </a>
                        </li> */}
                    </ul>
                    <p>Follow Me</p>
                </div>
            </header>
            <div className={`app-sidenav ${displaySidenav && "active"}`}>
                <div className="sidenav-menu">
                    <div className="logo">
                        <Link to={`url`.replace(/([^:])(\/\/+)/g, "$1/")}>Ally Deeter</Link>
                    </div>
                    <button className="button" onClick={(e) => handleClick(e)}></button>
                    <ul className="sidenav-nav">
                        {links.map((item, index) => (
                            <li key={index} onClick={(e) => handleClick(e)}>
                                <NavLink to={`${url}${item.path}`.replace(/([^:])(\/\/+)/g, "$1/")} activeClassName="active">
                                    <span className="anchor">{item.title}</span>
                                </NavLink>
                            </li>
                        ))}
                    </ul>
                    <div className="sidenav-links">
                        <ul>
                            {/* <li>
                                <a href="!#">
                                    <FaTwitter className="icon" />
                                </a>
                            </li> */}
                            <li>
                                <a href="https://github.com/Ardeeter" target="_blank" rel="noopener noreferrer">
                                    <FaGithub className="icon" />
                                </a>
                            </li>
                            <li>
                                <a href="http://www.linkedin.com/in/ally-deeter" target="_blank" rel="noopener noreferrer">
                                    <FaLinkedin className="icon" />
                                </a>
                            </li>
                        </ul>
                        <p>Follow Me</p>
                    </div>
                </div>
                <span className="sidenav-close" />
            </div>
            {/* <DemoOptions /> */}
        </>
    );
}

export default Header;
